cards.code_example:
body {
  font-family: Arial, sans-serif;
  background-color: lightblue;
}

document.querySelector('button').addEventListener('click', function() {
  alert('Klik registreret!');
});

function helloWorld() {
  console.log('Hej, verden!');
}

helloWorld();

function sum(a, b) {
  return a + b;
}

console.log(sum(2, 3));

nav {
  display: flex;
  justify-content: space-between;
}

nav a {
  flex: 1;
  text-align: center;
}

.grid-container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: auto;
  grid-gap: 10px;
}

const myEvent = new CustomEvent('myCustomEvent', { detail: { message: 'Custom event udløst' } });
document.querySelector('#myElement').addEventListener('myCustomEvent', function (e) {
  console.log(e.detail.message);
});
document.querySelector('#myElement').dispatchEvent(myEvent);

document.querySelector('#parentElement').addEventListener('click', function (event) {
  if (event.target.matches('.childElement')) {
    console.log('Et barn-element blev klikket på.');
  }
});

async function fetchData(url) {
  try {
    const response = await fetch(url);
    const data = await response.json();
    console.log(data);
  } catch (error) {
    console.error('Error:', error);
  }
}

fetchData('https://api.example.com/data');

const myPromise = new Promise((resolve, reject) => {
  setTimeout(() => {
    resolve('Success!');
  }, 2000);
});

myPromise
  .then(result => console.log(result))
  .catch(error => console.error(error));

const socket = new WebSocket('ws://example.com/socket');

socket.addEventListener('open', (event) => {
  socket.send('Hello, Server!');
});

socket.addEventListener('message', (event) => {
  console.log('Message from server:', event.data);
});

.container {
  display: flex;
  justify-content: center;
  align-items: center;
}

.centered-element {
  width: 100px;
  height: 100px;
}

function handleClick() {
  console.log('Element clicked!');
}

const element = document.querySelector('#myElement');
element.addEventListener('click', handleClick);
element.removeEventListener('click', handleClick);

const anonymousFunction = function() {
  console.log('Dette er en anonym funktion.');
};

function namedFunction() {
  console.log('Dette er en navngivet funktion.');
}

@media (max-width: 768px) {
  .navbar {
    display: flex;
    flex-direction: column;
  }
}

@media (min-width: 769px) {
  .navbar {
    display: flex;
    flex-direction: row;
  }
}

document.querySelector('#parent').addEventListener('click', function(event) {
  console.log('Target:', event.target);
  console.log('CurrentTarget:', event.currentTarget);
});

(function() {
  const localVar = 'I am in an IIFE';
  console.log(localVar);
})();

.myElement {
  background-color: blue;
}

document.querySelector('#myElement').addEventListener('click', function() {
  console.log('Element clicked!');
});

function greet(name) {
  console.log('Hello, ' + name + '!');
}
